css:
body {
    background-color: gainsboro;
    margin: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
  }

  #first-container {
    background-color: white;
    padding: 50px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
    text-align: center;
    position: relative;
    width: 140%;
    height:500px;
    margin: 0px 0px 0px -90px;


    background-image: url('https://staging.one-fm.com//files/481.jpg'); 
    background-size: cover; 
    background-repeat: no-repeat;
    background-position: center; 
  
  }

  #inner-container {
    margin: 30px 0px 30px 0px;    
  

  }

  #first-container > div {
    margin-bottom: 10px;
    position: relative;
  }

  .certificate-image {
    width: 80px;
    height: 80px;
    position: absolute;
    top: 0;
    left: 50; 
    opacity:0.8 ;
  }

  hr {
    border: none;
    height: 1px;
    background-color: black;
    margin: 10px auto;
    width: 50%;
  }

  .name-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 0px;
  }

  .name-container > div {
    flex-basis: 45%;
  }

  .name {
    font-weight: bold;
    font-family: 'Arial', sans-serif;
    font-size: 13px;
    margin: 5px 0;
  }

  .certificate-title {
    font-family: 'Lucida Handwriting', cursive;
    font-size: 36px;
    color: #333;
    margin-bottom: 0px;
  }

  .certificate-subtitle {
    font-weight: bold;
    font-size: 18px;
    color: darkblue;
  }

  .bold-text {
    font-weight: bold;
  }

  .thick-text {
    font-weight: 600;
  }

  .name-container img {
    max-width: 80px;
    height: auto;
    opacity:0.6;
  }
